<?php	if(!defined("_VALID_PHP"))		die('Direct access to this location is not allowed.');	$sfset=$pdo->query("SELECT * FROM `cs_site_settings` ORDER BY `settings_id` ASC LIMIT 1");		$rfset=$sfset->fetch(PDO::FETCH_ASSOC);			$default_name=@$_POST['default_name'];	$default_page=@$_POST['default_page'];	$default_email=@$_POST['default_email'];	$default_name=@$_POST['default_name'];	$default_template=@$_POST['default_template'];	$default_lang=@$_POST['default_lang'];	$default_pay=@$_POST['default_pay'];	$page_null_az=@$_POST['page_null_az'];	$page_null_ru=@$_POST['page_null_ru'];	$page_null_en=@$_POST['page_null_en'];    $site_hot_line=@$_POST['hot_line'];	$ok_settings=@$_POST['ok_settings'];	$err=0;		global $errors;		$f_mail=preg_match('|([a-z0-9_\.\-]{1,20})@([a-z0-9\.\-]{1,20})\.([a-z]{2,4})|is', $default_email);		if(isset($ok_settings)){		if($_SESSION['token'] != $_POST['token']) {        die();    } else {        $_SESSION['token'] = '';    }		if(!$default_name || !$default_lang){			$errors.='Заполните все поля';			$err++;		}				if($err==0){						$pdo->query("UPDATE `cs_site_settings` SET			`site_default_name`=".$pdo->quote($default_name).",			`site_default_lang_dir`=".$pdo->quote($default_lang).",			`page_null_az`=".$pdo->quote($page_null_az).",			`page_null_ru`=".$pdo->quote($page_null_ru).",			`site_hot_line`=".$pdo->quote($site_hot_line).",			`page_null_en`=".$pdo->quote($page_null_en).",			`site_default_email`=".$pdo->quote($default_email)." WHERE `settings_id`=1 LIMIT 1");						?>			<script>				alert('Сохранено успешно.');				window.location="index.php?page=site_settings";			</script>			<?php					}			}	$token = md5(uniqid(mt_rand() . microtime()));$_SESSION['token'] = $token;?><div class="content-box">		<div class="content-box-header">		<h3>Параметры сайта</h3>		<div class="clear"></div>	</div>	<div class="content-box-content">			<?php			if(isset($ok_settings) AND $err>0){			?>			<div class="error_div">			<?php echo $errors; ?>			</div>			<?php			}		?>					<form method="POST">				<p>				<label>Название сайта</label>				<input class="text-input small-input" type="text" value="<?php echo $rfset['site_default_name']; ?>" name="default_name" />			</p>						<p>				<label>E-mail</label>				<input class="text-input small-input" type="text" value="<?php echo $rfset['site_default_email']; ?>" name="default_email" />			</p>						<p>				<label>Язык по умолчанию</label>				<select name="default_lang" class="small-input">				<?php												$sflang=$pdo->query("SELECT * FROM `cs_language_list` ORDER BY `language_id` ASC");						while($rflang=$sflang->fetch(PDO::FETCH_ASSOC)){							$select_lang=($rfset['site_default_lang_dir']==$rflang['language_dir'])?'SELECTED="SELECTED"':'';						?>						<option <?php echo $select_lang; ?> value="<?php echo $rflang['language_dir']; ?>"><?php echo $rflang['language_name']; ?></option>						<?php						}				?>				</select> 			</p>			<p>				<input name="token" type="hidden" value="<?php echo $token;?>" />				<input class="button" type="submit" name="ok_settings" value="Сохранить" />			</p>				</form>					<div class="clear"></div>	</div></div>