INSERT INTO `translates` (`id`, `ref_table`, `ref_id`, `lang`, `fieldname`, `text`) VALUES
(2429, 'news', 119, 'az', 'full', '<p style=\"text-align: justify;\">Azərbaycanın d&ouml;vlət atributları və Lənkəranın simvolunu əks etdirən fotostendlərlə bəzədilən parkda &ldquo;&Ccedil;ayımızın adı var&rdquo;, &ldquo;S&uuml;frələrin bəzəyi - Lənkəranın d&uuml;y&uuml;s&uuml;&rdquo;, &ldquo;G&ouml;zəlliyi damla-damla topladıq&rdquo;, &ldquo;Yeni Lənkəran tikmək &uuml;&ccedil;&uuml;n&rdquo;, &ldquo;Lənkəranın şirnisi-ağız dadımız&rdquo;, &ldquo;Cənnət g&ouml;rmək istəyən Lənkərana gəlsin&rdquo; və digər pavilyonlarla tanış olublar. Rayonda istehsal olunan ərzaq, sənaye, kənd təsərr&uuml;fatı məhsulları, xalq sənətkarlığı n&uuml;munələri, yerli təbii &ccedil;ay və m&uuml;xtəlif şirniyyatlar, &ccedil;ərəzlərdən ibarət &ldquo;&ccedil;ay dəstgahı&rdquo; qonaqların diqqətini &ccedil;əkib.<br />\r\nTədbir iştirak&ccedil;ılarına y&uuml;ksəkkeyfiyyətli yerli sortlardan olan &ldquo;Haşimi&rdquo;, &ldquo;Sədri&rdquo;, &ldquo;Campo&rdquo; d&uuml;y&uuml;lərindən bişirilən 50 n&ouml;vdən &ccedil;ox plov, eyni zamanda, Lənkəran mətbəxinin ləziz təamı - ləvəngidən hazırlanan x&ouml;rəklər və m&uuml;xtəlif &ccedil;eşidli kabablar təqdim olunub.<br />\r\nA&ccedil;ılış mərasimində &ccedil;ıxış edən Lənkəran Şəhər İcra Hakimiyyətinin baş&ccedil;ısı Taleh Qaraşov lənkəranlıları &ouml;lkəmizdə ilk dəfə ke&ccedil;irilən &ldquo;&Ccedil;ay, &ccedil;əltik və sitrus festivalı&rdquo; m&uuml;nasibətilə təbrik edib.<br />\r\nPrezident İlham Əliyevin sədrliyi ilə bu il sentyabrın 3-də Lənkəran şəhərində &ccedil;ay, &ccedil;əltik və sitrus meyvələri istehsalının inkişafı məsələlərinə dair respublika m&uuml;şavirəsinin ke&ccedil;irildiyini və bu m&uuml;şavirənin nəticəsi olaraq həmin sahələrin inkişafı ilə bağlı D&ouml;vlət Proqramının qəbul edildiyini vurğulayan T.Qaraşov bu g&uuml;n Lənkəranın &ouml;z &ldquo;qızıl d&ouml;vr&uuml;n&uuml;&rdquo; yaşadığını diqqətə &ccedil;atdırıb.<br />\r\nMərasimdə 2017-ci ildə aqrar b&ouml;lmədə uğur qazanmış məhsul istehsal&ccedil;ılarının bir qrupu fəxri fərmanlar və qiymətli hədiyyələrlə m&uuml;kafatlandırılıb.<br />\r\nG&uuml;nboyu davam edən festivalda bədii &ouml;zfəaliyyət kollektivləri rəngarəng, ədəbi-bədii kompozisiyalarla &ccedil;ıxış edib, maraqlı musiqi n&ouml;mrələri səsləndirib, gənclərin iştirakı ilə əyləncəli proqramlar təqdim olunub.<br />\r\nFestival respublikanın tanınmış incəsənət ustalarının ifasında bayram konserti və atəşfəşanlıqla başa &ccedil;atıb.<br />\r\nAZƏRTAC</p>\r\n\r\n<p style=\"text-align: justify;\"><img alt=\"\" src=\"http://aqrobiznes.az/upload/albums/l1.jpg\" style=\"width: 500px; height: 281px;\" /></p>\r\n\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n\r\n<p style=\"text-align: justify;\"><img alt=\"\" src=\"http://aqrobiznes.az/upload/albums/l2.jpg\" style=\"width: 500px; height: 333px;\" /><br />\r\n&nbsp;</p>'),
(2430, 'news', 119, 'az', 'short', 'Azərbaycanın dövlət atributları və Lənkəranın simvolunu əks etdirən fotostendlərlə bəzədilən parkda “Çayımızın adı var”, “Süfrələrin bəzəyi - Lənkəranın düyüsü”, “Gözəlliyi damla-damla topladıq”, “Yeni Lənkəran tikmək üçün”, “Lənkəranın şirnisi-ağız dadımız”, “Cənnət görmək istəyən Lənkərana gəlsin” və digər pavilyonlarla tanış olublar. Rayonda istehsal olunan ərzaq, sənaye, kənd təsərrüfatı məhsulları, xalq sənətkarlığı nümunələri, yerli təbii çay və müxtəlif şirniyyatlar, çərəzlərdən ibarət “çay dəstgahı” qonaqların diqqətini çəkib.'),
