(5669, 'news', 306, 'az', 'title', 'XOCALI SOYQIRIMINDAN 26 İL ÖTÜR'),
(5670, 'news', 306, 'az', 'full', '<p><span style=\"font-family:tahoma,geneva,sans-serif;\"><span style=\"font-size:16px;\">Bu g&uuml;n 1992-ci il fevralın 25-dən 26-na ke&ccedil;ən gecə erməni təcav&uuml;zkarları tərəfindən t&ouml;rədilmiş Xocalı Soyqırımının n&ouml;vbəti ild&ouml;n&uuml;m&uuml;d&uuml;r. </span></span></p>\r\n\r\n<p><span style=\"font-family:tahoma,geneva,sans-serif;\"><span style=\"font-size:16px;\">K&uuml;tləvi qırğın nəticəsində Xocalı şəhərinin 106-sı qadın, 63-&uuml; uşaq, 70-i qoca olmaqla 613 nəfər sakini amansızlıqla qətlə yetirildi, 487 nəfər şikəst oldu, 1275 dinc sakin girov g&ouml;t&uuml;r&uuml;ld&uuml;, onlardan 150 nəfərin taleyi hələ də məlum deyil. Bu soyqırım aktı nəticəsində 8 ailə tamamilə məhv edildi, 25 uşaq hər iki valideynini itirdi. Hər il d&uuml;nya azərbaycanlıları soyqırım qurbanlarının xatirəsini h&ouml;rmətlə yad edirlər.</span></span></p>\r\n\r\n<p><span style=\"font-family:tahoma,geneva,sans-serif;\"><span style=\"font-size:16px;\">&ldquo;Ərzaq məhsullarının tədar&uuml;k&uuml; və təchizatı&rdquo; ASC-nin kollektivi də bu g&uuml;n soyqırım qurbanlarının xatirəsinə ehtiramını bildirərək Xətai rayonundakı &ldquo;Ana fəryadı&rdquo; abidəsini ziyarət etdi, &ouml;n&uuml;nə g&uuml;l dəstələri d&uuml;zd&uuml;lər. </span></span></p>\r\n\r\n<p><span style=\"font-family:tahoma,geneva,sans-serif;\"><span style=\"font-size:16px;\"><img alt=\"\" src=\"/upload/2222.JPG\" style=\"width: 750px; height: 500px;\" /></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-family:tahoma,geneva,sans-serif;\"><span style=\"font-size:16px;\"><img alt=\"\" src=\"/upload/3.JPG\" style=\"width: 750px; height: 500px;\" /></span></span></p>\r\n\r\n<p><img alt=\"\" src=\"/upload/4.JPG\" style=\"width: 750px; height: 500px;\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-family:tahoma,geneva,sans-serif;\"><span style=\"font-size:16px;\"><img alt=\"\" src=\"/upload/5.JPG\" style=\"width: 750px; height: 500px;\" /></span></span></p>'),
